mapper.select:
SELECT * FROM coupon


        AND status = #{status}


        AND (name LIKE CONCAT('%', #{keyword}, '%') OR description LIKE CONCAT('%', #{keyword}, '%'))


ORDER BY create_time DESC

    LIMIT #{page}, #{size}

SELECT COUNT(*) FROM coupon


        AND status = #{status}


        AND (name LIKE CONCAT('%', #{keyword}, '%') OR description LIKE CONCAT('%', #{keyword}, '%'))

mapper.update:
UPDATE coupon
name = #{name}, description = #{description}, type = #{type}, value = #{value}, min_amount = #{minAmount}, max_discount = #{maxDiscount}, total_count = #{totalCount}, used_count = #{usedCount}, per_user_limit = #{perUserLimit}, start_time = #{startTime}, end_time = #{endTime}, status = #{status}, update_time = NOW()
WHERE id = #{id}

UPDATE coupon
SET status = #{status}, update_time = NOW()
WHERE id = #{id}
