mapper.select:
SELECT 
FROM `order`
WHERE user_id = #{userId, jdbcType=BIGINT}

    AND status = #{status, jdbcType=VARCHAR}

ORDER BY create_time DESC

    LIMIT #{size, jdbcType=INTEGER} OFFSET #{page, jdbcType=INTEGER}

SELECT 
FROM `order`
WHERE 1=1

    AND user_id = #{userId, jdbcType=BIGINT}


    AND device_id IN (
        SELECT id FROM device WHERE store_id = #{storeId, jdbcType=BIGINT}
    )


    AND status = #{status, jdbcType=VARCHAR}


    AND create_time >= #{startTime, jdbcType=VARCHAR}


    AND create_time <= #{endTime, jdbcType=VARCHAR}

ORDER BY create_time DESC

    LIMIT #{size, jdbcType=INTEGER} OFFSET #{page, jdbcType=INTEGER}

SELECT COUNT(*)
FROM `order`
WHERE user_id = #{userId, jdbcType=BIGINT}

    AND status = #{status, jdbcType=VARCHAR}

SELECT COUNT(*)
FROM `order`
WHERE 1=1

    AND user_id = #{userId, jdbcType=BIGINT}


    AND device_id IN (
        SELECT id FROM device WHERE store_id = #{storeId, jdbcType=BIGINT}
    )


    AND status = #{status, jdbcType=VARCHAR}


    AND create_time >= #{startTime, jdbcType=VARCHAR}


    AND create_time <= #{endTime, jdbcType=VARCHAR}

mapper.update:
UPDATE `order` SET status = #{status, jdbcType=VARCHAR},
pay_time = #{payTime, jdbcType=TIMESTAMP},
update_time = NOW() WHERE order_id = #{orderId, jdbcType=VARCHAR}
